public Dictionary<string, string> GetRuleViolations(DataRow row)\n{\n    var errors = new Dictionary<string, string>();\n\n    // Check for required fields\n    foreach (DataColumn column in row.Table.Columns)\n    {\n        if (column.AllowDBNull == false && row[column] == DBNull.Value)\n        {\n            errors.Add(column.ColumnName, "Field is required");\n        }\n    }\n\n    // Check for data type errors\n    foreach (DataColumn column in row.Table.Columns)\n    {\n        try\n        {\n            Convert.ChangeType(row[column], column.DataType);\n        }\n        catch (FormatException ex)\n        {\n            errors.Add(column.ColumnName, "Invalid data type");\n        }\n    }\n\n    // Add custom validation rules here...\n\n    return errors;\n}