using System;\nusing System.Security.Cryptography.X509Certificates;\n\nclass CustomCertificateStore\n{\n    static void Main(string[] args)\n    {\n        string storeName = "MyAppCertificateStore";\n        string storeLocation = @"C:\Certs";\n\n        X509Store store = new X509Store(storeName, StoreLocation.LocalMachine);\n        store.Open(OpenFlags.ReadWrite);\n\n        // Create the custom store if it doesn't exist\n        if (!store.Exists())\n        {\n            store.Create();\n        }\n\n        // Add a certificate to the custom store\n        X509Certificate2 certificate = new X509Certificate2(@"C:\Path\To\Certificate.cer");\n        store.Add(certificate);\n\n        store.Close();\n    }\n}