public abstract class Event { /* common properties */ }\npublic class ShiftEvent : Event { /* shift-specific properties */ }\npublic class StaffEvent : Event { /* staff-specific properties */ }\n\npublic class EventFactory {\n    public static Event CreateEvent(DataRow row) {\n        switch (row["EventType"].ToString()) {\n            case "Shift":\n                return new ShiftEvent(row);\n            case "Staff":\n                return new StaffEvent(row);\n            // ...\n        }\n    }\n}