using System.IdentityModel.Tokens.Jwt;\nusing System.Security.Claims;\nusing Microsoft.IdentityModel.Tokens;\n\n// Generate a JWT token with the user ID\nvar token = new JwtSecurityToken(\n    issuer: "your_issuer",\n    audience: "your_audience",\n    claims: new[]\n    {\n        new Claim(ClaimTypes.NameIdentifier, userId.ToString())\n    },\n    notBefore: DateTime.UtcNow,\n    expires: DateTime.UtcNow.AddMinutes(30),\n    signingCredentials: new SigningCredentials(new SymmetricSecurityKey(your_secret_key), SecurityAlgorithms.HmacSha256)\n);\n\n// Send the JWT token to the client\nreturn new JsonResult(new { token = new JwtSecurityTokenHandler().WriteToken(token) });