// Example of a simple Google Maps API request\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\npublic class MapsExample\n{\n    public async Task<string> GetGoogleMapsResponse(string apiKey, string address)\n    {\n        using (var client = new HttpClient())\n        {\n            var request = $"https://maps.googleapis.com/maps/api/geocode/json?address={address}&key={apiKey}";\n            var response = await client.GetAsync(request);\n            return await response.Content.ReadAsStringAsync();\n        }\n    }\n}