using System;\nusing System.Security.Cryptography;\n\npublic static string GenerateRandomFolderName(int length = 12)\n{\n    using (var rng = RandomNumberGenerator.Create())\n    {\n        var bytes = new byte[length];\n        rng.GetBytes(bytes);\n        return Convert.ToBase62String(bytes);\n    }\n}\n\npublic static string ConvertToBase62String(byte[] bytes)\n{\n    const string base62Chars = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";\n    var result = new char[bytes.Length];\n    for (int i = 0; i < bytes.Length; i++)\n    {\n        result[i] = base62Chars[bytes[i] % base62Chars.Length];\n    }\n    return new string(result);\n}