// Calculate the scaling factor\nfloat scaleFactorX = (float)PreviewSize.Width / SceneSize.Width;\nfloat scaleFactorY = (float)PreviewSize.Height / SceneSize.Height;\n\n// Loop through the scene objects\nforeach (SceneObject obj in Scene)\n{\n    PictureBox p = new PictureBox();\n\n    // Scale down the object size\n    p.Size = new Size((int)(obj.Size.Width * scaleFactorX), (int)(obj.Size.Height * scaleFactorY));\n\n    // Scale down the object location\n    p.Location = new Point((int)(obj.Location.X * scaleFactorX), (int)(obj.Location.Y * scaleFactorY));\n\n    // Add the picture box to the preview window\n    previewWindow.Controls.Add(p);\n}