using Microsoft.Win32;\n\n// Opening the key in read-only mode\nRegistryKey myKey = Registry.LocalMachine.OpenSubKey("SOFTWARE\\MyCompany\\MyAppName", false);\n\nif (myKey != null)\n{\n    // Read the values\n    foreach (string valueName in myKey.GetValueNames())\n    {\n        object value = myKey.GetValue(valueName);\n        Console.WriteLine("Value Name: {0}, Value: {1}", valueName, value);\n    }\n    myKey.Close();\n}\nelse\n{\n    Console.WriteLine("Could not open registry key.");\n}