using System;\nusing System.IO;\nusing DSOFile;\n\nclass OfficeFileReader\n{\n    public static void Main(string[] args)\n    {\n        string folderPath = @"C:\Path\To\Office\Files";\n        string[] files = Directory.GetFiles(folderPath);\n\n        foreach (string file in files)\n        {\n            if (file.EndsWith(".doc") || file.EndsWith(".ppt") || file.EndsWith(".xls"))\n            {\n                OleDocumentProperties props = new OleDocumentProperties();\n                props.Open(file, false, dsoFileOpenOptions.dsoOptionDefault);\n\n                string author = props.SummaryProperties.Author;\n\n                Console.WriteLine($"Author of {Path.GetFileName(file)}: {author}");\n\n                props.Close();\n            }\n        }\n    }\n}