public class MyClass\n{\n    // Private field is List<T> for internal flexibility\n    private List<string> internalStrings = new List<string>();\n\n    // Public property is IList<T> for controlled exposure\n    public IList<string> PublicStrings { get; private set; }\n\n    public MyClass()\n    {\n        PublicStrings = internalStrings.AsReadOnly(); // Expose readonly list to prevent external modifications\n    }\n\n    // Method that modifies the internal collection safely\n    public void AddInternalString(string newString)\n    {\n        internalStrings.Add(newString);\n    }\n}