using System;\nusing System.Data.OleDb;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            OleDbEnumerator enumerator = new OleDbEnumerator();\n            var providers = enumerator.GetElements();\n            foreach (var provider in providers)\n            {\n                if (provider == "Microsoft.Jet.OLEDB.4.0")\n                {\n                    Console.WriteLine("Microsoft.Jet.OLEDB.4.0 provider is installed");\n                    return;\n                }\n            }\n            Console.WriteLine("Microsoft.Jet.OLEDB.4.0 provider is not installed");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}