public class YourComObjectWrapper : IDisposable\n{\n    private YourComObject _comObject;\n\n    public YourComObjectWrapper(YourComObject comObject)\n    {\n        _comObject = comObject;\n    }\n\n    public void Dispose()\n    {\n        if (_comObject != null)\n        {\n            _comObject.Dispose();\n            Marshal.ReleaseComObject(_comObject);\n            _comObject = null;\n        }\n    }\n}