using CozyRoc.SSIS.SFTP;\n\n// Create a new SFTP connection\nSftpConnection connection = new SftpConnection();\nconnection.Host = "sftp.example.com";\nconnection.Port = 22;\nconnection.Username = "username";\nconnection.Password = "password";\n\n// Create a new SFTP task\nSftpTask task = new SftpTask();\ntask.Connection = connection;\ntask.Operation = SftpOperation.Upload;\ntask.LocalPath = @"C:\local\path\file.txt";\ntask.RemotePath = "/remote/path/file.txt";\n\n// Execute the task\ntask.Execute();