using System;\nusing System.Text;\n\npublic class DictionaryKeyHelper\n{\n    public static string ValidateAndNormalizeKey(string input)\n    {\n        if (input == null)\n        {\n            throw new ArgumentNullException(nameof(input));\n        }\n\n        // Remove leading and trailing whitespace\n        input = input.Trim();\n\n        // Normalize the string to NFC form\n        input = input.Normalize(NormalizationForm.FormC);\n\n        // Check for null characters\n        if (input.Contains("\0"))\n        {\n            throw new ArgumentException("Input contains null characters", nameof(input));\n        }\n\n        // Enforce a reasonable length limit (e.g., 1024 characters)\n        if (input.Length > 1024)\n        {\n            throw new ArgumentException("Input exceeds maximum length", nameof(input));\n        }\n\n        return input;\n    }\n}