// DAL interface\npublic interface IDal\n{\n    void Insert(string tableName, object data);\n    // ...\n}\n\n// SQL Server DAL implementation\npublic class SqlServerDAL : IDal\n{\n    public void Insert(string tableName, object data)\n    {\n        // SQL Server-specific implementation\n    }\n}\n\n// Oracle DAL implementation\npublic class OracleDAL : IDal\n{\n    public void Insert(string tableName, object data)\n    {\n        // Oracle-specific implementation\n    }\n}\n\n// Factory class\npublic class DalFactory\n{\n    public static IDal CreateDal(string databaseType)\n    {\n        switch (databaseType)\n        {\n            case "SqlServer":\n                return new SqlServerDAL();\n            case "Oracle":\n                return new OracleDAL();\n            default:\n                throw new ArgumentException("Unsupported database type");\n        }\n    }\n}\n\n// Usage\nstring databaseType = "SqlServer"; // or "Oracle"\nIDal dal = DalFactory.CreateDal(databaseType);\ndal.Insert("myTable", myData);