using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing Newtonsoft.Json;\n\npublic class DatamuseApi\n{\n    private const string ApiUrl = "https://api.datamuse.com/words";\n\n    public async Task<string[]> GetWordsByLength(int length)\n    {\n        using var httpClient = new HttpClient();\n        var response = await httpClient.GetAsync($"{ApiUrl}?sp=*{new string('?', length)}");\n\n        if (response.IsSuccessStatusCode)\n        {\n            var responseBody = await response.Content.ReadAsStringAsync();\n            var words = JsonConvert.DeserializeObject<DatamuseWord[]>(responseBody);\n            return words.Select(w => w.word).ToArray();\n        }\n\n        return Array.Empty<string>();\n    }\n}\n\npublic class DatamuseWord\n{\n    public string word { get; set; }\n}