private IQueryable<ComputerAttribute> FilterByMemory(IQueryable<ComputerAttribute> query, int minMemory)\n{\n    return query.Where(ca => ca.Attribute == "MEMORY" && int.Parse(ca.Value) >= minMemory);\n}\n\nprivate IQueryable<ComputerAttribute> FilterByCpuSpeed(IQueryable<ComputerAttribute> query, float minCpuSpeed)\n{\n    return query.Where(ca => ca.Attribute == "CPU" && float.Parse(ca.Value) >= minCpuSpeed);\n}