using System.Data.SqlClient;\n\n// Assuming you have a SqlConnection object named 'connection'\nSqlConnection connection = new SqlConnection("Your Connection String");\n\n// Backup script\nstring backupScript = "BACKUP DATABASE [YourDatabaseName] TO DISK = 'C:\\Backup\\YourDatabaseName.bak'";\n\n// Restore script\nstring restoreScript = "RESTORE DATABASE [YourDatabaseName] FROM DISK = 'C:\\Backup\\YourDatabaseName.bak'";\n\n// Create a SqlCommand object\nSqlCommand command = new SqlCommand(backupScript, connection);\n\n// Open the connection\nconnection.Open();\n\n// Execute the backup script\ncommand.ExecuteNonQuery();\n\n// Close the connection\nconnection.Close();\n\n// Repeat the same steps for the restore script\ncommand = new SqlCommand(restoreScript, connection);\nconnection.Open();\ncommand.ExecuteNonQuery();\nconnection.Close();