DirectoryEntry dirEnt = new DirectoryEntry();\n\nusing (DirectorySearcher srch = new DirectorySearcher(dirEnt, "(cn=Administrators)"))\n{\n    srch.PageSize = 1000;\n    SearchResultCollection results = srch.FindAll();\n\n    foreach (SearchResult result in results)\n    {\n        DirectoryEntry groupEntry = result.GetDirectoryEntry();\n        PropertyValueCollection members = groupEntry.Properties["member"];\n\n        foreach (string member in members)\n        {\n            Console.WriteLine(member);\n        }\n    }\n}