// Domain Model\npublic class User\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public string Email { get; set; }\n}\n\n// View Model\npublic class UserViewModel\n{\n    public string Name { get; set; }\n    public string Email { get; set; }\n}\n\n// Mapping\npublic class UserMapper\n{\n    public UserViewModel Map(User user)\n    {\n        return new UserViewModel\n        {\n            Name = user.Name,\n            Email = user.Email\n        };\n    }\n}