using System.Data;\nusing System.Data.SqlServerCe;\n\n// Create a typed DataSet\npublic class MyDataSet : DataSet\n{\n    public MyDataSet()\n    {\n        // Initialize the DataSet structure\n    }\n}\n\n// Create a SQL Server CE connection\nusing (SqlCeConnection conn = new SqlCeConnection("Data Source=mydatabase.sdf"))\n{\n    conn.Open();\n\n    // Fill the DataSet with data from the database\n    MyDataSet dataSet = new MyDataSet();\n    SqlCeDataAdapter adapter = new SqlCeDataAdapter("SELECT * FROM mytable", conn);\n    adapter.Fill(dataSet);\n\n    // Bind the DataSet to a WPF control (e.g., a DataGrid)\n    myDataGrid.ItemsSource = dataSet.Tables["mytable"].DefaultView;\n}