using System.Runtime.Serialization;\n\n// Create a custom serialization class\n[DataContract]\npublic class RichTextData\n{\n    [DataMember]\n    public string Text { get; set; }\n\n    [DataMember]\n    public string FontFamily { get; set; }\n\n    [DataMember]\n    public double FontSize { get; set; }\n\n    // Add other necessary properties\n}\n\n// Saving\nRichTextData data = new RichTextData\n{\n    Text = richTextBox.Document.Blocks.First().Text,\n    FontFamily = richTextBox.FontFamily.ToString(),\n    FontSize = richTextBox.FontSize\n};\nDataContractSerializer serializer = new DataContractSerializer(typeof(RichTextData));\nserializer.WriteObject(stream, data);\n\n// Loading\nRichTextData data = serializer.ReadObject(stream) as RichTextData;\nFlowDocument flowDocument = new FlowDocument();\nflowDocument.Blocks.Add(new Paragraph(new Run(data.Text) { FontFamily = new FontFamily(data.FontFamily), FontSize = data.FontSize }));