// In the Subsonic.DatabaseProvider class, add a new method\npublic string GetSchemaOwner(string tableName)\n{\n    // This will depend on your database provider\n    // For example, in SQL Server, you can use the following:\n    using (DbCommand cmd = this.CreateCommand())\n    {\n        cmd.CommandText = "SELECT s.name FROM sys.tables t INNER JOIN sys.schemas s ON t.schema_id = s.schema_id WHERE t.name = @tableName";\n        cmd.Parameters.AddWithValue("@tableName", tableName);\n        object result = cmd.ExecuteScalar();\n        return result.ToString();\n    }\n}