// Third-party model\npublic class ExternalModel {\n    public string Name { get; set; }\n    public int Age { get; set; }\n}\n\n// View model\npublic class MyViewModel {\n    [DisplayName("Full Name")]\n    public string Name { get; set; }\n\n    [Required]\n    public int Age { get; set; }\n\n    // Constructor to map external model to view model\n    public MyViewModel(ExternalModel externalModel) {\n        Name = externalModel.Name;\n        Age = externalModel.Age;\n    }\n}