using System.Text.RegularExpressions;\n\npublic string GenerateStrongPassword(int length)\n{\n    string password = Membership.GeneratePassword(length, 1);\n    string passwordStrengthRegex = Membership.PasswordStrengthRegularExpression;\n\n    while (!Regex.IsMatch(password, passwordStrengthRegex))\n    {\n        password = Membership.GeneratePassword(length, 1);\n    }\n\n    return password;\n}