// Example: Get the text from a window with a custom class using UIA\nusing System.Windows.Automation;\n\nclass Program {\n    static void Main(string[] args) {\n        AutomationElement root = AutomationElement.RootElement;\n        AutomationElement window = root.FindFirst(\n            TreeScope.Children,\n            new PropertyCondition(AutomationElement.ClassNameProperty, "CustomClass")\n        );\n        if (window != null) {\n            ValuePattern pattern = window.GetCurrentPattern(ValuePattern.Pattern) as ValuePattern;\n            if (pattern != null) {\n                string text = pattern.Current.Value;\n                Console.WriteLine(text);\n            }\n        }\n    }\n}