using System.DirectoryServices;\n\n// Define the LDAP path to the security group\nstring groupPath = "LDAP://CN=GroupName,OU=OrganizationalUnit,DC=Domain,DC=com";\n\n// Define the LDAP path to the user\nstring userPath = "LDAP://CN=UserName,OU=OrganizationalUnit,DC=Domain,DC=com";\n\n// Create a new DirectoryEntry object for the security group\nDirectoryEntry groupEntry = new DirectoryEntry(groupPath);\n\n// Create a new DirectoryEntry object for the user\nDirectoryEntry userEntry = new DirectoryEntry(userPath);\n\n// Add the user to the security group\ngroupEntry.Properties["member"].Add(userEntry.Path);\n\n// Commit the changes\ngroupEntry.CommitChanges();\n\n// Close the DirectoryEntry objects\ngroupEntry.Close();\nuserEntry.Close();