using System.Security.Cryptography;\nusing System.Text;\n\n// Create a hash of the stored procedure code\nstring procedureCode = "SELECT * FROM table";\nbyte[] hashBytes = SHA256.Create().ComputeHash(Encoding.UTF8.GetBytes(procedureCode));\nstring hash = BitConverter.ToString(hashBytes).Replace("-", "");\n\n// Store the hash in your application\n\n// Later, when executing the stored procedure...\nstring procedureName = "my_procedure";\nstring hashFromDatabase = GetHashFromDatabase(procedureName);\n\nif (hashFromDatabase != hash)\n{\n    throw new SecurityException("Stored procedure has been tampered with");\n}\n\n// Execute the stored procedure