using System;\nusing System.Runtime.InteropServices;\n\nclass NTPClient\n{\n    [DllImport("W32tm.dll")]\n    private static extern int W32tmSetTime(string serverName);\n\n    public static void SyncTime(string ntpServer)\n    {\n        int result = W32tmSetTime(ntpServer);\n        if (result != 0)\n        {\n            Console.WriteLine("Time synchronization failed with error code " + result);\n        }\n    }\n}