using System.Drawing;\nusing System.IO;\n\n// Assume 'jpegData' is a byte array containing the JPEG image data\nbyte[] jpegData = ...;\n\nusing (MemoryStream ms = new MemoryStream(jpegData))\n{\n    using (Bitmap bitmap = new Bitmap(ms))\n    {\n        // Now you have a Bitmap object in memory, which you can pass to your third-party library\n        // You can also access the bitmap data directly using the LockBits method\n        Rectangle rect = new Rectangle(0, 0, bitmap.Width, bitmap.Height);\n        BitmapData data = bitmap.LockBits(rect, ImageLockMode.ReadOnly, PixelFormat.Format24bppRgb);\n        // Use the 'data' object to access the bitmap data\n        bitmap.UnlockBits(data);\n    }\n}