using System.DirectoryServices;\n\n// Get the IIS metabase\nDirectoryEntry iisMetabase = new DirectoryEntry("IIS://localhost/W3SVC/1/Root");\n\n// Get the MIME types collection\nDirectoryEntry mimeTypes = iisMetabase.Children.Find("MimeMap");\n\n// Add a new MIME type\nDirectoryEntry newMimeType = mimeTypes.Children.Add("application/pdf", ".pdf");\n\n// Commit the changes\nnewMimeType.CommitChanges();