public interface IArtifactMetadata\n{\n    string Application { get; }\n}\n\n[ImportMany(typeof(ExtendedArtifactBase), AllowRecomposition = true)]\nprivate Lazy<ExtendedArtifactBase, IArtifactMetadata>[] _artifacts { get; set; }\n\npublic ExtendedArtifactBase GetArtifact(string applicationName)\n{\n    return _artifacts\n        .FirstOrDefault(a => a.Metadata.Application.Equals(applicationName))\n        ?.Value;\n}