public class CustomClaimsPrincipal : ClaimsPrincipal\n{\n    public CustomClaimsPrincipal(CustomClaimsIdentity identity) : base(identity)\n    {\n    }\n}\n\npublic class CustomClaimsIdentity : ClaimsIdentity\n{\n    public int UserId { get; set; }\n    public string Username { get; set; }\n    public int RoleId { get; set; }\n    public string RoleName { get; set; }\n    public List<Permission> Permissions { get; set; }\n\n    public CustomClaimsIdentity(int userId, string username, int roleId, string roleName, List<Permission> permissions)\n        : base("Custom")\n    {\n        UserId = userId;\n        Username = username;\n        RoleId = roleId;\n        RoleName = roleName;\n        Permissions = permissions;\n    }\n}\n\npublic class Permission\n{\n    public int ObjectId { get; set; }\n    public bool Read { get; set; }\n    public bool Write { get; set; }\n    public bool Delete { get; set; }\n    public bool Create { get; set; }\n}