using System;\nusing System.ServiceModel;\nusing System.Data;\n\nnamespace AccessDatabaseWrapper\n{\n    [ServiceContract]\n    public interface IAccessDatabase\n    {\n        [OperationContract]\n        DataTable GetData(string query);\n    }\n\n    public class AccessDatabase : IAccessDatabase\n    {\n        private string connectionString;\n\n        public AccessDatabase(string connectionString)\n        {\n            this.connectionString = connectionString;\n        }\n\n        public DataTable GetData(string query)\n        {\n            // Connect to the Access database and execute the query\n            using (OleDbConnection connection = new OleDbConnection(connectionString))\n            {\n                connection.Open();\n                OleDbCommand command = new OleDbCommand(query, connection);\n                OleDbDataAdapter adapter = new OleDbDataAdapter(command);\n                DataTable table = new DataTable();\n                adapter.Fill(table);\n                return table;\n            }\n        }\n    }\n}