using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("Netapi32.dll", CharSet = CharSet.Unicode)]\n    static extern int DsGetDcName(string computerName, string domainName, ref IntPtr domainControllerName, ref int domainControllerNameLength);\n\n    static void Main()\n    {\n        string domainName = "example.com"; // your domain name\n        IntPtr domainControllerName = IntPtr.Zero;\n        int domainControllerNameLength = 0;\n\n        int result = DsGetDcName(null, domainName, ref domainControllerName, ref domainControllerNameLength);\n        if (result == 0)\n        {\n            string ldapServerName = Marshal.PtrToStringAuto(domainControllerName);\n            Console.WriteLine("LDAP server name: " + ldapServerName);\n        }\n        else\n        {\n            Console.WriteLine("Error: " + result);\n        }\n    }\n}