using System.Text.RegularExpressions;\n\npublic class SqlParser\n{\n    public static IEnumerable<string> ExtractUpdateColumns(string sql)\n    {\n        var pattern = new Regex(@"SET\s+([^\s,=\]]+)(?:\s*=\s*[^\s,]+)(?:,\s*([^\s,=\]]+)(?:\s*=\s*[^\s,]+))*", RegexOptions.IgnoreCase);\n        var match = pattern.Match(sql);\n        if (match.Success)\n        {\n            var columns = match.Groups[1].Captures.Cast<Capture>().Select(c => c.Value.Trim('[', ']'));\n            return columns;\n        }\n        return Enumerable.Empty<string>();\n    }\n}