[Table(Name = "MyEntity")]\npublic class MyEntity\n{\n    [Column(IsPrimaryKey = true, IsDbGenerated = true)]\n    public int Id { get; set; }\n\n    [Column(IsPrimaryKey = true)]\n    public int Version { get; set; }\n\n    // other properties...\n}\n\n[Table(Name = "MyEntity")]\npublic class MyEntityVersion : MyEntity\n{\n    // this class represents a specific version of MyEntity\n}\n\npublic class MyEntityRepository\n{\n    private DataContext _context;\n\n    public MyEntityRepository(DataContext context)\n    {\n        _context = context;\n    }\n\n    public MyEntity GetLatestVersion(int id)\n    {\n        return _context.GetTable<MyEntity>()\n            .Where(e => e.Id == id)\n            .OrderByDescending(e => e.Version)\n            .FirstOrDefault();\n    }\n\n    public void Save(MyEntity entity)\n    {\n        // create a new version of the entity\n        var newVersion = new MyEntityVersion\n        {\n            Id = entity.Id,\n            Version = entity.Version + 1,\n            // copy other properties...\n        };\n\n        _context.GetTable<MyEntityVersion>().InsertOnSubmit(newVersion);\n        _context.SubmitChanges();\n    }\n}