using System.IO;\n\n// Open the file\nusing (FileStream fs = new FileStream("path/to/your/assembly.dll", FileMode.Open, FileAccess.ReadWrite))\n{\n    // Read the file header\n    byte[] header = new byte[0x40];\n    fs.Read(header, 0, header.Length);\n\n    // Get the Characteristics field\n    ushort characteristics = BitConverter.ToUInt16(header, 0x16);\n\n    // Set the 32BIT flag\n    characteristics |= 0x0100;\n\n    // Write the modified Characteristics field back to the file\n    byte[] modifiedHeader = new byte[header.Length];\n    Array.Copy(header, modifiedHeader, header.Length);\n    BitConverter.GetBytes(characteristics).CopyTo(modifiedHeader, 0x16);\n    fs.Position = 0;\n    fs.Write(modifiedHeader, 0, modifiedHeader.Length);\n}