protected void Application_BeginRequest(object sender, EventArgs e)\n{\n    string url = Request.Url.AbsoluteUri;\n    if (url.StartsWith("http://myserver/something/"))\n    {\n        int id = int.Parse(url.Split('/')[url.Split('/').Length - 1]);\n        string targetUrl = "http://someotherserver/applications/something/modules/default.aspx?id=" + id;\n        Response.Redirect(targetUrl, true);\n    }\n}