List<MyObject> myList = new List<MyObject>();\n\n// populate and sort the list\n\nMyObject target = new MyObject();\n\nint min = 0;\nint max = myList.Count - 1;\n\nwhile (min <= max)\n{\n    int mid = (min + max) / 2;\n    MyObject current = myList[mid];\n\n    if (current.Equals(target))\n    {\n        // found it\n        break;\n    }\n    else if (current.CompareTo(target) < 0)\n    {\n        min = mid + 1;\n    }\n    else\n    {\n        max = mid - 1;\n    }\n}