using Microsoft.Extensions.DependencyInjection;\nusing MyApp.Interfaces;\nusing MyApp.Core.Data;\nusing MyApp.Core.BusinessLogic;\n\nnamespace MyApp.Host\n{\n    public class Program\n    {\n        public static void Main(string[] args)\n        {\n            var services = new ServiceCollection();\n            services.AddTransient<IDataAccess, DataAccess>();\n            services.AddTransient<IBusinessLogic, BusinessLogic>();\n\n            var serviceProvider = services.BuildServiceProvider();\n\n            // Resolve the dependencies\n            var dataAccess = serviceProvider.GetService<IDataAccess>();\n            var businessLogic = serviceProvider.GetService<IBusinessLogic>();\n        }\n    }\n}