using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Xml;\n\nclass XmlLoader\n{\n    public static void LoadXmlFile(string xmlFilePath, string connectionString)\n    {\n        // Create a DataTable to store the data\n        DataTable dataTable = new DataTable();\n        dataTable.Columns.Add("Item", typeof(string));\n        dataTable.Columns.Add("Link", typeof(string));\n\n        // Load the XML file\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.Load(xmlFilePath);\n\n        // Iterate through the XML nodes and populate the DataTable\n        foreach (XmlNode itemNode in xmlDoc.SelectNodes("/TopNode/Item"))\n        {\n            string item = itemNode.Attributes["id"].Value;\n            foreach (XmlNode linkNode in itemNode.SelectNodes("Link"))\n            {\n                dataTable.Rows.Add(item, linkNode.InnerText);\n            }\n        }\n\n        // Use SqlBulkCopy to load the DataTable into the SQL Server table\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            using (SqlBulkCopy bulkCopy = new SqlBulkCopy(connection))\n            {\n                bulkCopy.DestinationTableName = "YourTableName";\n                bulkCopy.WriteToServer(dataTable);\n            }\n        }\n    }\n}