using System;\nusing System.Security.Cryptography;\nusing System.Security.Cryptography.X509Certificates;\nusing System.IO;\n\n// Load the certificate from the .pfx file\nvar cert = new X509Certificate2("path/to/your/certificate.pfx", "password");\n\n// Get the private key from the certificate\nvar privateKey = cert.GetRSAPrivateKey();\n\n// Create a new key container\nvar keyContainer = new CspKeyContainerInfo();\nkeyContainer.KeyContainerName = Guid.NewGuid().ToString();\nkeyContainer.MachineKeyStore = true;\n\n// Create a new RSA key using the private key\nvar rsaKey = RSA.Create();\nrsaKey.ImportParameters(privateKey.ExportParameters(true));\n\n// Add the key to the key container\nvar key = new RSACryptoServiceProvider(keyContainer);\nkey.ImportParameters(rsaKey.ExportParameters(true));\n\n// Add the certificate to the X509Store\nvar store = new X509Store(StoreName.My, StoreLocation.LocalMachine);\nstore.Open(OpenFlags.ReadWrite);\nstore.Add(cert);\nstore.Close();