using (FileStream fileInStream = new FileStream(inputFile, FileMode.Open, FileAccess.Read))\n{\n    using (BufferedStream bufferedInStream = new BufferedStream(fileInStream))\n    {\n        using (StreamReader reader = new StreamReader(bufferedInStream, new EBCDICEncoding()))\n        {\n            while (!reader.EndOfStream)\n            {\n                char[] charArray = new char[52];\n                reader.Read(charArray, 0, charArray.Length);\n\n                foreach (char letter in charArray)\n                {\n                    int value = Convert.ToInt16(letter);\n                    string hexOut = string.Format("{0:x}", value);\n                    Debug.WriteLine(hexOut);\n                }\n            }\n        }\n    }\n}