using System;\nusing System.Web;\nusing System.Web.SessionState;\n\npublic class CustomSessionIDManager : SessionIDManager\n{\n    public override string CreateSessionID(HttpContext context)\n    {\n        // Create a unique session ID for each application\n        string sessionId = base.CreateSessionID(context);\n        string appName = context.Request.ApplicationPath.ToLower();\n        return appName + "_" + sessionId;\n    }\n\n    public override bool Validate(string id)\n    {\n        // Validate the session ID for each application\n        string[] parts = id.Split('_');\n        if (parts.Length != 2)\n        {\n            return false;\n        }\n        string appName = HttpContext.Current.Request.ApplicationPath.ToLower();\n        return parts[0] == appName && base.Validate(parts[1]);\n    }\n}