public class OracleSqlGenerator : SqlGenerator\n{\n    // ...\n\n    public override string CreateTable(TableSchema.Table table)\n    {\n        // ...\n\n        if (table.Columns.Count > 0)\n        {\n            // ...\n\n            if (table.PrimaryKey != null)\n            {\n                sql.AppendLine($"  CONSTRAINT {table.PrimaryKey.Name} PRIMARY KEY ({string.Join(", ", table.PrimaryKey.Columns.Select(c => c.Name))})");\n            }\n        }\n\n        // ...\n    }\n}