using System.Text.RegularExpressions;\n\npublic static string RemoveHtmlTags(string text)\n{\n    return Regex.Replace(text, "<.*?>", string.Empty);\n}\n\npublic static string RemoveUnwantedCharacters(string text)\n{\n    return Regex.Replace(text, @"[^\w\s]", string.Empty);\n}\n\n// Usage:\nstring originalText = "Your RSS text with HTML tags and unwanted characters";\nstring cleanText = RemoveHtmlTags(originalText);\ncleanText = RemoveUnwantedCharacters(cleanText);