using System.Net.Security;\nusing System.Net.Sockets;\n\nclass SslServer\n{\n    public static void StartServer(X509Certificate2 certificate)\n    {\n        var listener = new TcpListener(IPAddress.Any, 443);\n        listener.Start();\n\n        while (true)\n        {\n            var client = listener.AcceptTcpClient();\n            using (var sslStream = new SslStream(client.GetStream(), false, ValidateServerCertificate))\n            {\n                sslStream.AuthenticateAsServer(certificate);\n                // Handle remoting packets here\n            }\n        }\n    }\n\n    private static bool ValidateServerCertificate(object sender, X509Certificate certificate, X509Chain chain, SslPolicyErrors sslPolicyErrors)\n    {\n        return true; // Allow self-signed certificates\n    }\n}