using System;\nusing System.Text;\n\npublic class EBCDICEncoding : Encoding\n{\n    private static readonly byte[] ebcdicToUnicode = new byte[256];\n\n    static EBCDICEncoding()\n    {\n        // Initialize the mapping from EBCDIC to Unicode\n        ebcdicToUnicode[0x5A] = 0x21; // !\n        ebcdicToUnicode[0x40] = 0x20; // Space\n        // Add more mappings as needed...\n    }\n\n    public override int GetByteCount(char[] chars, int index, int count)\n    {\n        return count;\n    }\n\n    public override int GetBytes(char[] chars, int charIndex, int charCount, byte[] bytes, int byteIndex)\n    {\n        for (int i = 0; i < charCount; i++)\n        {\n            bytes[byteIndex + i] = ebcdicToUnicode[chars[charIndex + i]];\n        }\n        return charCount;\n    }\n\n    public override int GetCharCount(byte[] bytes, int index, int count)\n    {\n        return count;\n    }\n\n    public override int GetChars(byte[] bytes, int byteIndex, int byteCount, char[] chars, int charIndex)\n    {\n        for (int i = 0; i < byteCount; i++)\n        {\n            chars[charIndex + i] = (char)ebcdicToUnicode[bytes[byteIndex + i]];\n        }\n        return byteCount;\n    }\n\n    public override int GetMaxByteCount(int charCount)\n    {\n        return charCount;\n    }\n\n    public override int GetMaxCharCount(int byteCount)\n    {\n        return byteCount;\n    }\n}