using System;\n\nclass Program\n{\n    static void Main()\n    {\n        // Assuming the datetime from the database is in UTC\n        DateTime utcDateTime = new DateTime(2009, 5, 10, 0, 0, 0);\n\n        // Specify the target timezone\n        TimeZoneInfo targetTimeZone = TimeZoneInfo.FindSystemTimeZoneById("Central European Standard Time");\n\n        // Convert to the target timezone\n        DateTime targetDateTime = TimeZoneInfo.ConvertTimeFromUtc(utcDateTime, targetTimeZone);\n\n        Console.WriteLine(targetDateTime);\n    }\n}